package javaBasicsLoops;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		printnumbers();

	}
	
	
	public static void printnumbers() {
		int i=5;
		do {
			System.out.println(i);
			i++;
		}while(i<=10);

		
		
	}

}

